segment codeglobal _get_scan_codeglobal _get_key_press; uint8 get_scan_code();; Returns a scan code of a keypress, this is useful for dealing with characters such as; arrow keys or shift_get_scan_code:push bpmov bp, spmov ah, 1int 0x16mov al, ahxor ah, ahpush axcall _clear_keyboard_bufferpop axpop bpret; uint8 get_key_press();; Returns a keycode that was pressed_get_key_press:push bpmov bp, spmov ah, 1int 0x16xor ah, ahpush axcall _clear_keyboard_bufferpop axpop bpret; void clear_keyboard_buffer();; Clears the keyboard buffer_clear_keyboard_buffer:push bpmov bp, spmov ah, 0xcmov al, 9int 0x21pop bpret